parameters.functionCode:
// FILE TAXONOMY VALIDATOR
// Enforces the skeleton structure from FILE_TAXONOMY.md

const commits = items[0].json.commits || [];
const violations = [];
const validFiles = [];

// Define allowed paths
const RULES = {
  sop: /^\/CHRONICLE\/SOP\/.+\.md$/,
  python_core: /^\/abundancetoken\/07_Code\/The_Ark\/(core|api)\/.+\.py$/,
  python_tools: /^\/abundancetoken\/07_Code\/The_Ark\/tools\/.+\.py$/,
  tests: /^\/abundancetoken\/07_Code\/The_Ark\/tests\/test_.+\.py$/,
  web: /^\/abundancetoken\/07_Code\/The_Ark\/web\/.+\.(js|css|html|json)$/,
  modules: /^\/abundancetoken\/07_Code\/The_Ark\/web\/modules\/.+/,
  workflows: /^\/abundancetoken\/07_Code\/The_Ark\/n8n_workflows\/.+\.json$/,
  jules_mission: /^\/abundancetoken\/07_Code\/The_Ark\/JULES_.+\.md$/,
  backup: /^\/abundancetoken\/07_Code\/The_Ark\/backup\/.+/,
  logs: /^\/abundancetoken\/07_Code\/The_Ark\/logs\/.+/,
  furnace: /^\/furnace\/.+/,
  chronicle_scripts: /^\/CHRONICLE\/scripts\/.+\.sh$/
};

// Check each changed file
for (const commit of commits) {
  const files = [...(commit.added || []), ...(commit.modified || [])];

  for (const file of files) {
    let isValid = false;
    let matchedRule = null;

    for (const [ruleName, pattern] of Object.entries(RULES)) {
      if (pattern.test('/' + file)) {
        isValid = true;
        matchedRule = ruleName;
        break;
      }
    }

    // Allow root config files
    if (!isValid && /^[^/]+\.(md|json|sh|py|gitignore)$/.test(file)) {
      isValid = true;
      matchedRule = 'root_config';
    }

    if (isValid) {
      validFiles.push({ file, rule: matchedRule });
    } else {
      violations.push({ file, reason: 'Does not match any allowed path pattern' });
    }
  }
}

return [{
  json: {
    valid: violations.length === 0,
    violation_count: violations.length,
    valid_file_count: validFiles.length,
    violations: violations,
    valid_files: validFiles,
    commit_author: commits[0]?.author?.name || 'unknown',
    timestamp: new Date().toISOString()
  }
}];

